SELECT SUM(purchaseprice) AS total
FROM purchase
WHERE TO_CHAR(purchasedate, 'YYYY-MM-DD') = TO_CHAR(SYSDATE - 3, 'YYYY-MM-DD') AND gymNo = #{gymNo}

