mapper.select:
select m.*, c.custEmail, c.custName, c.custPhone from myschedule m
         left join cust c on m.custNo = c.custNo
where classNo = #{classNo}

mapper.update:
UPDATE mySchedule
SET schedulecompleted = '1'
WHERE myscheduleNo = #{myscheduleNo}

UPDATE mySchedule
SET schedulecompleted = '0'
WHERE myscheduleNo = #{myscheduleNo}
